import React, { useState, } from "react";
import "./bookingForm.scss";

const BookingForm = () => {
  // State for each form field
  const [date, setDate] = useState(" ");
  const [time, setTime] = useState(" ");
  const [guests, setGuests] = useState(1);
  const [occasion, setOccasion] = useState(" ");
  const [feedback, setFeedback] = useState(" ");
  const [isSubmitted, setIsSubmitted] = useState(" ");


  //State for available times - If available times are given ans set and change by the user input
  const availableTimes = ["17:00", "18:00", "19:00", "20:00", "21:00","22:00"];
// In a real app, It could be checked against a database or API
  const checkTimeAvailability = (selectedTime) => {
    return availableTimes.includes(selectedTime);
  };

  //Handles for each form field

  const handleDateChange = (e) => setDate(e.target.value);
  const handleTimeChange = (e) => setTime(e.target.value);
  const handleGuestsChange = (e) => setGuests(e.target.value);
  const handleOccasionChange = (e) => setOccasion(e.target.value);

  //Form Submit Handle
  const handleSubmit = (e) => {
    e.prevent.Default();
    if (checkTimeAvailability(time)){
        setFeedback('Your reservation submitted successfully.')
        setIsSubmitted(true);

        //Here, it would typically send the form data to a server. 
    } else {
        setFeedback('Selected time is not available.Please choose another time.')
        setIsSubmitted(false);
    }
    console.log({ date, time, guests, occasion });
  };

  return (
    <div className="form-container">
      <h1 className="reservation-header">Reservation Form</h1>
      <form className="reservation-form" onSubmit={handleSubmit}>
        <label htmlFor="res-date"> Choose date</label>
        <input
          type="date"
          id="res-date"
          value={date}
          onChange={handleDateChange}
          required
        />

        <label htmlFor="res-time">Choose time</label>
        <select id="res-time" value={time} onChange={handleTimeChange}>
          <option>17:00</option>
          <option>18:00</option>
          <option>19:00</option>
          <option>20:00</option>
          <option>21:00</option>
          <option>22:00</option>
        </select>

        <label htmlFor="guests">Number of guests</label>
        <input
          type="number"
          placeholder="1"
          min="1"
          max="10"
          id="guests"
          value={guests}
          onChange={handleGuestsChange}
        />

        <label htmlFor="occasion">Occasion</label>
        <select id="occasion" value={occasion} onChange={handleOccasionChange}>
          <option>Birthday</option>
          <option>Anniversary</option>
        </select>
        <button type="submit">Make Your Reservation</button>
      </form>
      {feedback && <p className={`feedback-message ${isSubmitted ? 'success' : 'error'}`}>{feedback}</p>}
    </div>
  );
};

export default BookingForm;


