import React from 'react'
import axios from "axios";
import { useEffect, useState } from "react";
import Result from "./Result";
import "./wheatherapp.scss";

const WheatherApp = () => {
    const key = "";
    const [search, setSearch] = useState("");
    const [data, setData] = useState();

    useEffect(() => {
      async function getWheather() {
        try {
          const response = await axios.get(
            `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=${key}&units=metric`
          );
          console.log(response);
          setData(response.data);
        } catch (error) {
          console.error(error);
        }
      }
      getWheather();
    }, [search]);
    console.log(search);
    return (
      <div className="App">
        <div className="container">
        <div className="container_header">
            <h1 className="header">Weather Forecast by City </h1>
          </div>
          <div className="input-group">

            <label className="input-group__label">My Label</label>
            <input
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Search by the City"
              type="text"
              id="myInput"
              className="input-group__input"
            />
          </div>
          {typeof data !== "undefined" && <Result data={data} />}
        </div>
      </div>
    );
  }
  export default WheatherApp;
